import React from 'react';
import 'antd/dist/antd.css';
import { Container } from 'react-bootstrap';
import { Form, Input, Button, Checkbox, Divider } from 'antd';
import Titulo from './protected/components/Titulo';
import api from '../src/service/api';
import {notificarErro} from '../src/protected/components/Notificacao';

const layout = {
    labelCol: {
        span: 5,
    },
    wrapperCol: {
        span: 16,
    },
};
const tailLayout = {
    wrapperCol: {
        offset: 5,
        span: 16,
    },
};

const forgoutLayout = {
    wrapperCol: {
        offset: 5,
    },
}

const Login = () => {
    const onFinish = async values => {
        try{
            const {cpfCnpj,senha} = values;
            const {data} = await api.get('/login',{params:{cpfCnpj,senha}});
            const {token} =  data;
            localStorage.setItem('token',token);
            const valorToken = await localStorage.getItem(token);
            const retorno = await api.get('/verificaToken',{params:{Authentication:valorToken}});
        }catch(e){
            notificarErro(e.response.data.mensagem);
        }
    };

    const onFinishFailed = errorInfo => {
        console.log('Failed:', errorInfo);
    };

    return (
        <div>
            <Titulo nome="Login" />
            <Divider />
            <Container className='Container'>
                <Form
                    {...layout}
                    name="basic"
                    initialValues={{
                        remember: true,
                    }}
                    onFinish={onFinish}
                    onFinishFailed={onFinishFailed}
                >
                    <Form.Item
                        label="Cpf/Cnpj"
                        name="cpfCnpj"
                        rules={[
                            {
                                required: true,
                                message: 'O CpfCnpj é obrigatório!',
                            },
                        ]}
                    >
                        <Input />
                    </Form.Item>

                    <Form.Item
                        label="Senha"
                        name="senha"
                        rules={[
                            {
                                required: true,
                                message: 'A senha é obrigatória!',
                            },
                        ]}
                    >
                        <Input.Password />
                    </Form.Item>

                    <Form.Item {...tailLayout}>
                        <Button type="primary" htmlType="submit" style={{margin:'2px'}}>
                            Entrar
                        </Button>
                        <Button type="danger" htmlType="button">
                            Esqueceu a senha?
                        </Button>
                    </Form.Item>
                </Form>
            </Container>
        </div>
    );
};

export default Login;
