import React, { useState, useEffect } from 'react';

import './index.css';

import { Table } from 'antd';

function TabelaPedidos({getData,acoes,handlerUpdateTable}) {

    const[loadingTable,setLoadingTable] = useState(false);
    const[lista,setLista] = useState({});

    useEffect(()=>{
        async function carregarDados(){
            setLoadingTable(true);
            const {data} = await getData();
            setLista(data);
            setLoadingTable(false);
        }
        carregarDados();
    },[getData,acoes,handlerUpdateTable]);

    const columns = [
        {
            title: 'Nº',
            dataIndex: 'id',
            key: 'id',

        },
        {
            title: 'Cliente',
            dataIndex: 'cliente',
            key: 'cliente',
        },
        {
            title: 'Data',
            dataIndex: 'data',
            key: 'data',
        },
        acoes,
    ];

    return (  
        <Table dataSource={lista} columns={columns} className='Tabela' pagination= { {pageSizeOptions: ['5','10','20', '40'], showSizeChanger: true,pageSize:5}} loading={loadingTable}/>   
    );
}

export default TabelaPedidos;
