    //         <FormUsuario cancelar={renderUsuarios} atualizar={true} editar={editarUsuario} usuarioUpdate={key}/>
    //         , document.getElementById('divUsuarioId'));
    // }



    const editarUsuario = async (usuario) => {
        try {
            await api.put('/usuario/',usuario);
            notificarSucesso('Usuário Atualizado com sucesso');
        } catch (e) {
            const { mensagem } = e.response.data;
            notificarErro(mensagem);
        }
    }

    return (
        <div>
            <Breadcrumb>
                <Item href="/">Principal</Item>
                <Item active>Usuarios</Item>
            </Breadcrumb>
            <Container className='Container'>
                <div id='divUsuarioId'>
                <TabelaUsuarios />
                </div>
            </Container>
        </div>
    );
}

export default Usuarios;
