import React, { useState, useEffect} from 'react';
import FormUsuario from '../../components/FormUsuario';
import {
    Divider,
} from 'antd';
import { Breadcrumb, Container } from 'react-bootstrap';
import Titulo from '../../components/Titulo';
const { Item } = Breadcrumb;


function PerfilUsuario({location}) {

    const [tipoUsuario, setTipoUsuario] = useState(0);
    const [key, setKey] = useState(''); 

    useEffect(() => {
        async function buscaUsuario() {
            if (location) {
                const params = new URLSearchParams(location.search);
                const k = params.get('key');
                const t = params.get('tipo');
                if(t){
                   await setTipoUsuario(parseInt(t));
                }

                if (k) {
                   await setKey(k);
                }

                setCarregado(true);
            }
        }
        buscaUsuario();
    }, [location]);

    return (
        <div>
            <Breadcrumb>
                <Item href="/">Principal</Item>
                <Item href="/usuarios">Usuários</Item>
                <Item active >Editar</Item>
            </Breadcrumb>
            <Container>
                <Titulo nome='Editar Usuário' />
                <Divider />
                {carregado && <FormUsuario tipoUsuario={tipoUsuario} key={key} atualizar/>}
            </Container>
        </div>
    );
}

export default PerfilUsuario;
