import React,{useState, useEffect} from 'react';
import { Breadcrumb, Container } from 'react-bootstrap';
import Titulo from '../../components/Titulo';
import TabelaHortalicas from '../../components/TabelaHortalicas';
import api from '../../../service/api';

import { Divider, Button } from 'antd';
const { Item } = Breadcrumb;

function Hortalicas() {

    const [lista, setLista] = useState('');
    const [loadingTable, setLoadingTable] = useState(false);
    const [handleUpdateTable, setHandleUpdateTable] = useState(true);

    useEffect(() => {
        async function carregaHortalicas() {
            setLoadingTable(true);
            const { data } = await api.get('/hortalica');
            setLista(data);
            setLoadingTable(false);
        }
        carregaHortalicas();
    }, [handleUpdateTable]);

    async function carregaHortalicas() {

        const { data } = await api.get('/hortalica');
       return data;
    }


    return (
        <div>
            <Breadcrumb>
                <Item href="/">Principal</Item>
                <Item active>Hortaliças</Item>
            </Breadcrumb>
            <Container>
                <Titulo nome='Hortaliças' />
                <Divider />
                <TabelaHortalicas dataSourceFunc={lista}/>
                <Divider />
                <Button type='primary' href='/perfilHortalica'>Cadastrar</Button>
            </Container>
        </div>
    );
}

export default Hortalicas;
