                      <Button type='primary' style={{marginLeft:'2px'}} danger
                      icon={<DeleteOutlined />}
                      >
                       Excluir
                      </Button>
                </span>
              ),
        },
    ];



    return (
        <Row>
             <Table dataSource={dataSource} columns={columns} className='Tabela'/>;
