import React, { useState, useEffect } from 'react';
import { notificarErro, notificarSucesso } from '../Notificacao/index';
import api from '../../../service/api';
import {
    Form,
    Button,
    Select,
    Input
} from 'antd';
import { useHistory } from 'react-router-dom';

function FormUsuario({ atualizar, location }) {

    const [usuario, setUsuario] = useState({});
    const [tipoCadastro, setTipoCadastro] = useState(0);
    const [carregado, setCarregado] = useState(false);

    const categorias = ['Funcionario', 'Cliente'];
    const history = useHistory();

    useEffect(() => {
        async function buscaUsuario() {
            if (location) {
                const params = new URLSearchParams(location.search);
                const k = params.get('key');
                const t = params.get('tipo');
                setTipoCadastro(t);
                if (k) {
                    try {
                        const { data } = await api.get(`/usuario/${k}`);
                        await setUsuario(data);
                        await setCarregado(true);
                    } catch (e) {

                    }
                }
            }
        }
        buscaUsuario();
    }, [location]);

    const cadastrarUsuario = async (usuario) => {
        try {
            await api.post('/usuario', { usuario });
            notificarSucesso('O usuário foi cadastrado com sucesso.');
            setTimeout(() => {
                history.push('/usuarios');
            }, 200);
        } catch (e) {
            const { mensagem } = e.response.data;
            notificarErro(mensagem);
        }
    }

    const editarUsuario = async (usuario) => {
        try {
            await api.put('/usuario', { usuario });
            notificarSucesso('O usuário foi atualizado com sucesso.');
            setTimeout(() => {
                history.push('/usuarios');
            }, 200);
        } catch (e) {
            const { mensagem } = e.response.data;
            notificarErro(mensagem);
        }
    }

    const onFormLayoutChange = ({ size }) => {
        console.log('formChange');
    };

    const onFinish = async (values) => {

        const { nome, funcao, cpfCnpj, senha, confirmSenha } = values;

        if (senha !== confirmSenha) {
            notificarErro('As senhas não são iguais');
        } else {

            const usuarioC = {
                nome,
                funcao,
                cpfCnpj,
                senha,
            };
            cadastrarUsuario(usuarioC);
        }
    };

    const onFinishEdit = async (values) => {
        const { nome, funcao, cpfCnpj, senha, confirmSenha } = values;
        if (senha && senha !== confirmSenha) {
            notificarErro('As senhas não são iguais');
        } else {
            if (nome && funcao && cpfCnpj) {
                const usuarioEdit = {
                    nome,
                    funcao,
                    cpfCnpj,
                    senha,
                };
                editarUsuario(usuarioEdit);
            }
        }
    };

    const onFinishFailed = errorInfo => {
        console.log('Failed:', errorInfo);
    };

    const layout = {
        labelCol: {
            span: 5,
        },
        wrapperCol: {
            span: 16,
        },
    };
    const tailLayout = {
        wrapperCol: {
            offset: 5,
            span: 16,
        },
    };
    if (carregado) {
        return (
            <div>
                <Form
                    {...layout}
                    name="basic"
                    initialValues={{
                        remember: true,
                        nome: usuario.nome,
                        funcao: usuario.funcao,
                        cpfCnpj: usuario.cpfCnpj
                    }}
                    onFinish={onFinishEdit}
                    onFinishFailed={onFinishFailed}
                    onValuesChange={onFormLayoutChange}
                    size='larger'>
                    <Form.Item
                        label='Nome'
                        name="nome">
                        <Input />
                    </Form.Item>
                    <Form.Item
                        label='Cpf/Cnpj'
                        name="cpfCnpj"


                    >
                        <Input readOnly />
                    </Form.Item>
                    <Form.Item
                        label='Função'
                        name="funcao"

                    >
                        <Select>
                            {
                                categorias.map((cat) => {
                                    return (<Select.Option value={cat} key={cat}>{cat}</Select.Option>)
                                })
                            }
                        </Select>
                    </Form.Item>

                    <Form.Item
                        label='Senha'
                        name='senha'
                    >
                        <Input.Password />
                    </Form.Item>

                    <Form.Item
                        label='Confirmação'
                        name="confirmSenha"
                    >
                        <Input.Password />
                    </Form.Item>

                    <Form.Item {...tailLayout}>
                        <Button
                            type="primary"
                            htmlType="submit">
                            Atualizar
                        </Button>

                        <Button
                            type="danger"
                            href="/usuarios"
                            style={{ marginLeft: '2px' }}
                        >
                            Cancelar
                        </Button>
                    </Form.Item>
                </Form>
            </div>);
    } else {

        return (
            <Form
                {...layout}
                name="basic"
                initialValues={{ remember: true }}
                onFinish={onFinish}
                onFinishFailed={onFinishFailed}
                onValuesChange={onFormLayoutChange}
                size='larger'>
                <Form.Item
                    label='Nome'
                    name="nome"
                    rules={[
                        {
                            required: true,
                            message: 'O nome é obrigatorio!'
                        }]}
                >
                    <Input id='nomeId' />
                </Form.Item>
                <Form.Item
                    label='Cpf/Cnpj'
                    name="cpfCnpj"
                    rules={[
                        {
                            required: true,
                            message: 'O CPF/Cnpj é obrigatorio!'
                        },
                        () => ({
                            validator(rule, value) {
                                let resolved = false;
                                if (!value) {
                                    return Promise.resolve();
                                }else if(value){
                                    console.log(tipoCadastro);
                                    if(tipoCadastro===0){
                                        resolved = value.match('^[0-9]{3}[.][0-9]{3}[.][0-9]{3}[-][0-9]{2}$');
                                        return Promise.resolve();
                                    }
                                }
                                return Promise.reject('O CPF não se encontra no formato: xxx.xxx.xxx-xx');
                            },
                        }),

                    ]}
                >
                    <Input />
                </Form.Item>

                <Form.Item
                    label='Função'
                    name="funcao"
                    rules={[
                        {
                            required: true,
                            message: 'A Função é obrigatoria!',
                        }]}
                >
                    <Select>
                        {
                            categorias.map((cat) => {
                                return (<Select.Option value={cat} key={cat}>{cat}</Select.Option>)
                            })
                        }
                    </Select>
                </Form.Item>

                <Form.Item
                    label='Senha'
                    name="senha"
                    rules={[
                        {
                            required: true,
                            message: 'A senha é obrigatoria!'
                        }]}
                >
                    <Input.Password />
                </Form.Item>

                <Form.Item
                    label='Confirmação'
                    name="confirmSenha"
                    rules={[
                        {
                            required: true,
                            message: 'A confirmação da senha é obrigatoria!'
                        },
                        ({ getFieldValue }) => ({
                            validator(rule, value) {
                                if (!value || getFieldValue('senha') === value) {
                                    return Promise.resolve();
                                }

                                return Promise.reject('As senhas não são iguais!');
                            },
                        }),
                    ]}
                >
                    <Input.Password />
                </Form.Item>

                <Form.Item {...tailLayout}>
                    <Button
                        type="primary"
                        htmlType="submit">
                        Salvar
                    </Button>
                    <Button
                        type="danger"
                        href="/usuarios"
                        style={{ marginLeft: '2px' }}
                    >
                        Cancelar
                        </Button>
                </Form.Item>
            </Form>
        );
    }
}

export default FormUsuario;
