import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import { ArrowRightOutlined, DeleteOutlined } from '@ant-design/icons';
import {
    Form,
    Input,
    Button,
    Divider,
    notification
} from 'antd';

import Menu from '../../components/Menu';
import { Breadcrumb, Container } from 'react-bootstrap';
import Titulo from '../../components/Titulo';
import TabelaHortalicas from '../../components/TabelaHortalicas';
const { Item } = Breadcrumb;


function CadastroPedidos() {
    const acoes = {
        title: 'Inserir',
        dataIndex: 'inserir',
        key: 'inserir',
        render: (text, record) => (
            <span>
                <Button
                    type='primary'
                    style={{ marginLeft: '2px' }}
                    icon={<ArrowRightOutlined />}
                    onClick={_ => {
                        inserirHortalica(record);
                    }}
                    title='Inserir'
                />
            </span>
        )
    }

    const openNotification = (msg, descricao) => {
        notification.open({
            message: `${msg}`,
            description:
                `${descricao}`,
        });
    };

    const acoesPedido = {
        title: 'Remover',
        dataIndex: 'remover',
        key: 'remover',
        render: (text, record) => (
            <span>
                <Button
                    type='primary'
                    style={{ marginLeft: '2px' }}
                    icon={<DeleteOutlined />}
                    danger
                    onClick={_ => {
                        alert(record)
                    }}
                    title='Remover'
                />
            </span>
        )
    }

    const history = useHistory();

    const layout = {
        labelCol: {
            span: 2,
        },
        wrapperCol: {
            span: 5,
        },
    };

    const tailLayout = {
        wrapperCol: {
            offset: 0,
            span: 5,
        },
    };

    const redirect = () => {
        history.goBack();
    }

    const hortalicasBKP = [
        {
            id: '1',
            nome: 'Alface',
            categoria: 'Folhas',
            classificacao: '5',
            valor: 3
        },
        {
            id: '2',
            nome: 'Coentro',
            categoria: 'Folhas',
            classificacao: '5',
            valor: 2,
        },
        {
            id: '3',
            nome: 'Cebola',
            categoria: 'Folhas',
            classificacao: '5',
            valor: 5,

        },
    ];


    const [data, setData] = useState('');
    const [valor, setValor] = useState(0);
    const [pedido, setPedido] = useState([]);
    const [hortalicas, setHortalicas] = useState([...hortalicasBKP]);

    state = {
        ModalText: 'Content of the modal',
        visible: false,
        confirmLoading: false,
      };

      showModal = () => {
        setVisible(false);
      };

      handleOk = () => {
        setConfirmLoading(true)
        setTimeout(() => {
            setVisible(false);
            setConfirmLoading(false);
        }, 2000);
      };

    const handleCancel = () => {
       setVisible(false);
      };

    const salvarPedido = () => {
        alert(data);
    }

    const inserirHortalica = (hortalica) => {
        let possui = false;
        pedido.map((hort) => {
            if (hort.id === hortalica.id) {
                possui = true;
            }
        });
        if (!possui) {
            setPedido([...pedido, hortalica])
        } else {
            openNotification('Aviso!', 'A hortaliça já foi adicionada!');
        }
    }



    return (
        <div>
            <Menu />
            <Breadcrumb>
                <Item href="/">Principal</Item>
                <Item href="/pedidos">Pedidos</Item>
                <Item active >Cadastrar</Item>
            </Breadcrumb>
            <Container>
                <Titulo name='Cadastro de Pedidos' />
                <Divider />
                <h2>Hortaliças</h2>
                <TabelaHortalicas dataSource={hortalicas} acoes={acoes} />
                <Divider />
                <h2>Pedido</h2>
                <TabelaHortalicas dataSource={pedido} acoes={acoesPedido} />
                <Divider />
                <Form {...layout}>
                    <Form.Item
                        {...tailLayout}
                        label='Valor Total: '>
                        <Input readOnly />
                    </Form.Item>
                    <Form.Item
                        {...tailLayout}
                        label='Data: '>
                        <Input value={data} onChange={(evnt) => { setData(evnt.target.value) }} />
                    </Form.Item>
                </Form>
                <Button type='primary  ' onClick={salvarPedido}>Salvar</Button>
                <Modal
                    title="Title"
                    visible={visible}
                    onOk={handleOk}
                    confirmLoading={confirmLoading}
                    onCancel={handleCancel}>
                    <p>Content</p>
                </Modal>
                <Divider />
            </Container>
        </div>
    );
}

export default CadastroPedidos;
