import React from 'react';
import {
    Row,
    Col,
    Button,
    ButtonToolbar,
} from 'react-bootstrap';

import './index.css';

import { Table, Tag } from 'antd';
const { Column, ColumnGroup } = Table;

function TabelaPedidos() {

    const dataSource = [
        {
            numero: '1',
            cliente: 'Samuel Vasconcelos',
            data: '15/04/2020',
            acoes:['ver','excluir']
        },
        {
            numero: '2',
            cliente: 'Samuel Vasconcelos',
            data: '15/04/2020',
            acoes:['ver','excluir']
        },
        {
            numero: '3',
            cliente: 'Samuel Vasconcelos',
            data: '15/04/2020',
            acoes:['ver','excluir']
        },
    ];

    const columns = [
        {
            title: 'Nº',
            dataIndex: 'numero',
            key: 'numero',

        },
        {
            title: 'Cliente',
            dataIndex: 'cliente',
            key: 'cliente',
        },
        {
            title: 'Data',
            dataIndex: 'data',
            key: 'data',
        },
        {
            title: 'Ações',
            dataIndex: 'acoes',
            key: 'acoes',
            render: acoes => (
                <span>
                  {acoes.map(tag => {
                    let color = tag.length > 5 ? 'geekblue' : 'green';
                    if (tag === 'loser') {
                      color = 'volcano';
                    }
                    return (
                      <Tag color={color} key={tag}>
                        {tag.toUpperCase()}
                      </Tag>
                    );
                  })}
                </span>
              ),
        },
    ];



    return (
        <Row>
             <Table dataSource={dataSource} columns={columns} className='Tabela'/>;
            {/* <Table striped bordered hover>
                <thead>
                    <tr>
                        <th width="5%">Nº</th>
