                    <tr>
                        <td>1</td>
                        <td>Samuel Pereira de Vasconcelos</td>
                        <td>11/04/2020</td>
                        <td>
                            <Col>
                                <ButtonToolbar>
                                    <Button variant="primary">Ver</Button>
                                    <Button variant="danger">Negar</Button>
                                </ButtonToolbar>
                            </Col>
                        </td>
                    </tr>
                </tbody>
            </Table> */}
        </Row>
    );
}

export default TabelaPedidos;
