import React,{ useState } from 'react';
import {
    Form,
    Input,
    Button,
    Radio,
    Select,
    Cascader,
    DatePicker,
    InputNumber,
    TreeSelect,
    Switch,
    Divider
  } from 'antd';

import Menu from '../../components/Menu';
import { Breadcrumb, Container } from 'react-bootstrap';
import Titulo from '../../components/Titulo';
const { Item } = Breadcrumb;

const { Option } = Select;
const formItemLayout = {
    labelCol: {
        span: 6,
    },
    wrapperCol: {
        span: 14,
    },
};

const normFile = e => {
    console.log('Upload event:', e);

    if (Array.isArray(e)) {
        return e;
    }

    return e && e.fileList;
};


function Cadastro() {
    const [componentSize, setComponentSize] = useState('small');
    const [categorias, setCategorias] = useState(['Folhas','Temperos','Legumes','Raizes']);

    const onFormLayoutChange = ({ size }) => {
        setComponentSize(size);
    };

    return (
        <div>
            <Menu />
            <Breadcrumb>
                <Item href="/">Principal</Item>
                <Item active>Hortaliças</Item>
            </Breadcrumb>
            <Container>
                <Titulo name='Cadastro de Hortaliças' />
                <Divider />
                <Form
                    labelCol={{
                        span: 4,
                    }}
                    wrapperCol={{
                        span: 14,
                    }}
                    layout="horizontal"
                    initialValues={{
                        size: 'larger',
                    }}
                    onValuesChange={onFormLayoutChange}
                    size='larger'>
                    <Form.Item label='Nome'>
                        <Input />
                    </Form.Item>
                    <Form.Item label='Categoria'>
                        <Select defaultValue={categorias[0]}>
                            {   
                                categorias.map((cat)=>{
                                    return(<Select.Option value={cat}>{cat}</Select.Option>)
                                })
                            }   
                        </Select>
                    </Form.Item>
                    <Form.Item label='Valor'>
                    <InputNumber  defaultValue={0}/>
                    </Form.Item>

                </Form>
            </Container>
        </div>
    );
}

export default Cadastro;
