import React, { useEffect, useState } from 'react';
import ReactDOM from 'react-dom';
import { Container, Breadcrumb } from 'react-bootstrap';
import Titulo from '../../components/Titulo';
import TabelaUsuarios from '../../components/TabelaUsuarios';
import { Divider, Button } from 'antd';
import FormUsuario from '../../components/FormUsuario';
import { notificarErro, notificarSucesso } from '../../components/Notificacao';
import api from '../../../service/api';
import './index.css';
const { Item } = Breadcrumb;

function Usuarios() {

    const renderCadastro = () => {
        ReactDOM.render(
            <div>
                <Titulo name="Cadastro de Usu치rios" />
                <Divider />
                <FormUsuario cadastrar={cadastrarUsuario} cancelar={renderUsuarios} update={false} />
                <Divider />
            </div>
            , document.getElementById('divUsuarioId'));
    }

    const renderUsuarios = () => {
        ReactDOM.render(
            <div>
                <TabelaUsuarios />
                <Button type='primary' onClick={renderCadastro}>Cadastrar</Button>
            </div>
            , document.getElementById('divUsuarioId'));
    }

    const cadastrarUsuario = async (usuario) => {
        try {
            await api.post('/usuario', { usuario });
            notificarSucesso('O usu치rio foi cadastrado com sucesso.');
            renderUsuarios();
        } catch (e) {
            const { mensagem } = e.response.data;
            notificarErro(mensagem);
        }
    }

    return (
        <div>
            <Breadcrumb>
                <Item href="/">Principal</Item>
                <Item active>Usuarios</Item>
            </Breadcrumb>
            <Container className='Container'>
                <div id='divUsuarioId'>
                    <Titulo name='Usu치rios' />
                    <Divider />
                    <TabelaUsuarios />
                    <Divider />
                    <Button type='primary' onClick={renderCadastro}>Cadastrar</Button>
                </div>
            </Container>
        </div>
    );
}

export default Usuarios;
