import React, { useState, useEffect } from 'react';

import './index.css';

import { Table } from 'antd';

function TabelaPedidos({getData,acoes,handlerUpdateTable}) {

    const[loadingTable,setLoadingTable] = useState(false);
    const[lista,setLista] = useState({});

    const dataSource = [
        {
            key: '1',
            nome: 'Samuel Vasconcelos',
            data: '15/04/2020',
        },
        {
            key: '2',
            nome: 'Samuel Vasconcelos',
            data: '15/04/2020',
        },
        {
            key: '3',
            nome: 'Samuel Vasconcelos',
            data: '15/04/2020',
        },
    ];

    useEffect(()=>{
        async function carregarDados(){
            setLoadingTable(true);
            const {data} = await getData();
            setLista({data});
            console.log(lista);
            setLoadingTable(false);
        }
        carregarDados();
    },[getData,acoes,handlerUpdateTable]);

    const columns = [
        {
            title: 'Nº',
            dataIndex: 'key',
            key: 'key',

        },
        {
            title: 'Cliente',
            dataIndex: 'nome',
            key: 'nome',
        },
        {
            title: 'Data',
            dataIndex: 'data',
            key: 'data',
        },
        acoes,
    ];

    return (  
      <Table columns={columns} dataSource={dataSource} loadingTable={loadingTable}/>
    );
}

export default TabelaPedidos;
