import React, { useState } from 'react';
import { notificarErro, notificarSucesso } from '../Notificacao/index';
import useHistory from 'react-router-dom';
import api from '../../../service/api';
import {
    Form,
    Input,
    Button,
    Select,
    Divider
} from 'antd';
import Titulo from '../Titulo';
import { Redirect } from 'react-router-dom';

function FormUsuario({atualizar }) {

    const [categorias, setCategorias] = useState(['Funcionario', 'Cliente']);
    const [usuario, setUsuario] = useState({});

    const historico = useHistory();

    // useEffect(() => {
    //     async function carregaUsuarios() {
    //         if (usuarioUpdate > 0)
    //             try {
    //                 const u = api.get(`/usuario/${usuarioUpdate}`);
    //                 setUsuario(u);
    //             } catch (e) {
    //                 const { mensagem } = e.response.data;
    //                 notificarErro(mensagem);
    //             }

    //     }
    //     carregaUsuarios();
    // }, []);

    const cadastrarUsuario = async (usuario) => {
        try {
            await api.post('/usuario', { usuario });
            notificarSucesso('O usuário foi cadastrado com sucesso.');
            setTimeout(() => {
                historico.goBack();
            },200);
        } catch (e) {
            const { mensagem } = e.response.data;
            notificarErro(mensagem);
        }

    }


    const onFormLayoutChange = ({ size }) => {
        console.log('formChange');
    };

    const onFinish = async (values) => {

        const { nome, funcao, cpfCnpj, senha, confirmSenha } = values;

        if (senha !== confirmSenha) {
            notificarErro('As senhas não são iguais');
        } else {

            const usuario = {
                nome,
                funcao,
                cpfCnpj,
                senha
            };
            cadastrarUsuario(usuario);
        }
    };



    const onFinishFailed = errorInfo => {
        console.log('Failed:', errorInfo);
    };

    const layout = {
        labelCol: {
            span: 5,
        },
        wrapperCol: {
            span: 16,
        },
    };
    const tailLayout = {
        wrapperCol: {
            offset: 5,
            span: 16,
        },
    };

    return (
        <div>
            {!atualizar && <Titulo nome="Cadastro de Usuários" />}
            {atualizar && <Titulo nome="Atualizar Usuário" />}
            <Divider />
            <Form
                {...layout}
                name="basic"
                initialValues={{
                    remember: true,
                    nome: usuario.nome,
                    funcao: usuario.funcao,
                    cpfCnpj: usuario.cpfCnpj
                }}
                onFinish={onFinish}
                onFinishFailed={onFinishFailed}
                onValuesChange={onFormLayoutChange}
                size='larger'>
                <Form.Item
                    label='Nome'
                    name="nome"
                    rules={[
                        {
                            required: true,
                            message: 'O nome é obrigatorio!'
                        }]}
                >
                    <Input />
                </Form.Item>
                <Form.Item
                    label='Cpf/Cnpj'
                    name="cpfCnpj"
                    rules={[
                        {
                            required: true,
                            message: 'O CPF/Cnpj é obrigatorio!'
                        }]}
                >
                    <Input />
                </Form.Item>
                <Form.Item
                    label='Função'
                    name="funcao"
                    rules={[
                        {
                            required: true,
                            message: 'A Função é obrigatoria!',
                        }]}
                >
                    <Select>
                        {
                            categorias.map((cat) => {
                                return (<Select.Option value={cat} key={cat}>{cat}</Select.Option>)
                            })
                        }
                    </Select>
                </Form.Item>

                <Form.Item
                    label='Senha'
                    name="senha"
                    rules={[
                        {
                            required: true,
                            message: 'A senha é obrigatoria!'
                        }]}
                >
                    <Input.Password />
                </Form.Item>

                <Form.Item
                    label='Confirmação'
                    name="confirmSenha"
                    rules={[
                        {
                            required: true,
                            message: 'A confirmação da senha é obrigatoria!'
                        }]}
                >
                    <Input.Password />
                </Form.Item>

                <Form.Item {...tailLayout}>
                    {atualizar &&
                        <Button
                            type="primary"
                            htmlType="submit">

                            Atualizar
                    </Button>
                    }
                    {!atualizar &&
                        <Button
                            type="primary"
                            htmlType="submit">
                            Salvar
                    </Button>
                    }
                    <Button
                        type="danger"
                        href="/usuarios"
                        style={{ marginLeft: '2px' }}
                    >
                        Cancelar
                        </Button>
                </Form.Item>
            </Form>
        </div>
    );
}

export default FormUsuario;
