import React, { useState } from 'react';
import './index.css';
import { Table, Button } from 'antd';
import Highlighter from 'react-highlight-words';
import { SearchOutlined } from '@ant-design/icons';

function TabelaHortalicas({ dataSource, acoes }) {
    const [searchText, setSearchText]  = useState('');
    const [searchedColumn, setSearchedColumn] = useState('');


    const getColumnSearchProps = dataIndex => ({
        filterDropdown: ({ setSelectedKeys, selectedKeys, confirm, clearFilters }) => (
            <div style={{ padding: 8 }}>
                <Input
                    ref={node => {
                        this.searchInput = node;
                    }}
                    placeholder={`Search ${dataIndex}`}
                    value={selectedKeys[0]}
                    onChange={e => setSelectedKeys(e.target.value ? [e.target.value] : [])}
                    onPressEnter={() => this.handleSearch(selectedKeys, confirm, dataIndex)}
                    style={{ width: 188, marginBottom: 8, display: 'block' }}
                />
                <Button
                    type="primary"
                    onClick={() => this.handleSearch(selectedKeys, confirm, dataIndex)}
                    icon={<SearchOutlined />}
                    size="small"
                    style={{ width: 90, marginRight: 8 }}
                >
                    Search
            </Button>
                <Button onClick={() => this.handleReset(clearFilters)} size="small" style={{ width: 90 }}>
                    Reset
            </Button>
            </div>
        ),
        filterIcon: filtered => <SearchOutlined style={{ color: filtered ? '#1890ff' : undefined }} />,
        onFilter: (value, record) =>
            record[dataIndex]
                .toString()
                .toLowerCase()
                .includes(value.toLowerCase()),
        onFilterDropdownVisibleChange: visible => {
            if (visible) {
                setTimeout(() => this.searchInput.select());
            }
        },
        render: text =>
            this.state.searchedColumn === dataIndex ? (
                <Highlighter
                    highlightStyle={{ backgroundColor: '#ffc069', padding: 0 }}
                    searchWords={[this.state.searchText]}
                    autoEscape
                    textToHighlight={text.toString()}
                />
            ) : (
                    text
                ),
    });

    const handleSearch = (selectedKeys, confirm, dataIndex) => {
        confirm();
        this.setState({
            searchText: selectedKeys[0],
            searchedColumn: dataIndex,
        });
    };

    const handleReset = clearFilters => {
        clearFilters();
        this.setState({ searchText: '' });
    };

    const columns = [
        {
            title: 'Codigo',
            dataIndex: 'key',
            key: 'key',
            defaultSortOrder: 'descend',
            sorter: (a, b) => a.key - b.key,

        },
        {
            title: 'Nome',
            dataIndex: 'nome',
            key: 'nome',
            defaultSortOrder: 'descend',
            sorter: (a, b) => ((a.nome > b.nome) ? 1 : ((b.nome > a.nome) ? -1 : 0))
        },
        {
            title: 'Categoria',
            dataIndex: 'categoria',
            key: 'categoria',
            defaultSortOrder: 'descend',
            sorter: (a, b) => ((a.categoria > b.categoria) ? 1 : ((b.categoria > a.categoria) ? -1 : 0)),
            fixed: 'left',
            filters: [
                {
                    text: 'Folhas',
                    value: 'Folhas',
                },
                {
                    text: 'Raizes',
                    value: 'Raizes',
                },
                {
                    text: 'Legumes',
                    value: 'Legumes',
                },
            ],
            onFilter: (value, record) => record.categoria.indexOf(value) === 0,
        },
        {
            title: 'Classificação',
            dataIndex: 'classificacao',
            key: 'classificacao',
            defaultSortOrder: 'descend',
            sorter: (a, b) => a.classificacao - b.classificacao,

        },
        {
            title: 'Valor',
            dataIndex: 'valor',
            key: 'valor',
            defaultSortOrder: 'descend',
            sorter: (a, b) => a.valor - b.valor,
        },
        acoes,
    ];

    return (
        <Table dataSource={dataSource} columns={columns} className='Tabela' />
    );
}

export default TabelaHortalicas;
