import api from './api';

const TOKEN = 'token';
const USUARIO = 'usuario';

const getKeyUsuarioLogado = () =>{
    return 3;
}

const isFuncionarioLogado = () => {
    return true;
}

const isClienteLogado = () => {
    return true;
}

const getToken = async () => {
    const token = sessionStorage.getItem(TOKEN);
    return token;
}

const getUsuarioCorrente = async () => {
    const usuario = await sessionStorage.getItem(USUARIO);
    return usuario;
}

const setToken = async (token) => {
    sessionStorage.setItem(TOKEN,token);
    const {data}= await api.get('/usuarioToken/', {params:{
        Authentication:token
    }});
    sessionStorage.setItem(USUARIO,data);
}

const sair = () => {
    sessionStorage.removeItem(TOKEN);
    sessionStorage.removeItem(USUARIO);
}

export {getKeyUsuarioLogado,isFuncionarioLogado,isClienteLogado,getToken,sair,setToken};
