import React, { useEffect, useState } from 'react';
import ReactDOM from 'react-dom';
import { Container, Breadcrumb } from 'react-bootstrap';
import Titulo from '../../components/Titulo';
import TabelaUsuarios from '../../components/TabelaUsuarios';
import { Divider, Button } from 'antd';
import FormUsuario from '../../components/FormUsuario';
import { notificarErro, notificarSucesso } from '../../components/Notificacao';
import api from '../../../service/api';
import './index.css';
const { Item } = Breadcrumb;

function Usuarios() {

    const renderCadastro = () => {
        ReactDOM.render(
            <FormUsuario cadastrar={cadastrarUsuario} cancelar={renderUsuarios} atualizar={false} />
            , document.getElementById('divUsuarioId'));
    }

    const renderUsuarios = () => {
        ReactDOM.render(
            <TabelaUsuarios cadastroHandler={renderCadastro} excluirHandler={excluirUsuario} editarHandler={renderEditar}/>
            , document.getElementById('divUsuarioId'));
    }

    const renderEditar = (key) => {
        ReactDOM.render(
            <FormUsuario cancelar={renderUsuarios} atualizar={true} editar={editarUsuario} usuarioUpdate={key}/>
            , document.getElementById('divUsuarioId'));
    }

    const cadastrarUsuario = async (usuario) => {
        try {
            await api.post('/usuario', { usuario });
            notificarSucesso('O usuário foi cadastrado com sucesso.');
            renderUsuarios();
        } catch (e) {
            const { mensagem } = e.response.data;
            notificarErro(mensagem);
        }
    }

    const excluirUsuario = async (key) => {
        try {
            await api.delete(`/usuario/${key}`);
            notificarSucesso('Usuário deletado com sucesso');
        } catch (e) {
            const { mensagem } = e.response.data;
            notificarErro(mensagem);
        }
    }

    const editarUsuario = async (usuario) => {
        try {
            await api.put('/usuario/',usuario);
            notificarSucesso('Usuário Atualizado com sucesso');
        } catch (e) {
            const { mensagem } = e.response.data;
            notificarErro(mensagem);
        }
    }

    return (
        <div>
            <Breadcrumb>
                <Item href="/">Principal</Item>
                <Item active>Usuarios</Item>
            </Breadcrumb>
            <Container className='Container'>
                <div id='divUsuarioId'>
                <TabelaUsuarios cadastroHandler={renderCadastro} excluirHandler={excluirUsuario} editarHandler={renderEditar}/>
                </div>
            </Container>
        </div>
    );
}

export default Usuarios;
