       openNotification();
       setTimeout(redirect, 1000);
    };

    const onFinishFailed = errorInfo => {
        console.log('Failed:', errorInfo);
    };

    const layout = {
        labelCol: {
            span: 5,
        },
        wrapperCol: {
            span: 16,
        },
    };
    const tailLayout = {
        wrapperCol: {
            offset: 5,
            span: 16,
        },
    };

    const openNotification = () => {
        notification.open({
          message: 'Salvo',
          description:
           'O usuário foi salvo com sucesso',
          onClick: () => {
            console.log('Notification Clicked!');
          },
        });
      };

    const redirect = () => {
        history.goBack();
    }

    return (
        <div>
            <Container>
                <Divider />

            </Container>
        </div>
    );

}

export default DadosCadastraisUsuario;
