import React,{useEffect,useState} from 'react';
import Menu from '../../components/Menu';
import { Container, Breadcrumb } from 'react-bootstrap';
import Titulo from '../../components/Titulo';
import { DeleteOutlined, EditOutlined } from '@ant-design/icons';
import TabelaUsuarios from '../../components/TabelaUsuarios';
import { Divider, Button } from 'antd';
import api from '../../../service/api';
import './index.css';
import { notificarErro, notificarSucesso } from '../../components/Notificacao';
const { Item } = Breadcrumb;

function Usuarios() {

    const acoes = {
        title: 'Ações',
        dataIndex: 'acoes',
        key: 'acoes',
        render: (text, record) => (
            <span>
                <Button
                    type='primary'
                    style={{ marginLeft: '2px' }}
                    icon={<EditOutlined />}
                    onClick={
                        _=>{editarUsuario(record)}
                    }
                />
                <Button
                    type='primary'
                    style={{ marginLeft: '2px' }}
                    danger
                    icon={<DeleteOutlined />}
                    onClick={
                        _=>{excluirUsuario(record.key)}
                    }
                />
            </span>
        ),
    }

    const editarUsuario = async (usuario) => {
        alert(usuario.key);
    }

    const excluirUsuario = async (key) => {
        try{
            await api.delete(`/usuario/${key}`);
            notificarSucesso('Usuário deletado com sucesso');
        }catch(e){
            const {mensagem} = e.response.data;
           notificarErro(mensagem);
        }
    }

return (
    <div>
        <Breadcrumb>
            <Item href="/">Principal</Item>
            <Item active>Usuarios</Item>
        </Breadcrumb>
        <Container className='Container'>
            <Titulo name='Usuários' />
            <Divider />
            <TabelaUsuarios acoes={acoes} />
            <Divider />
            <Button type='primary' href='/cadastroUsuarios'>Cadastrar</Button>
        </Container>

    </div>
);
}

export default Usuarios;
