import React, { useState, useEffect } from 'react';
import { notificarErro, notificarSucesso } from '../Notificacao/index';
import api from '../../../service/api';
import {
    Form,
    Button,
    Select,
    Input
} from 'antd';
import { Redirect } from 'react-router-dom';

function FormUsuario({ atualizar, location }) {

    const categorias = ['Funcionario', 'Cliente'];
    const [usuario, setUsuario] = useState({});
    const [redirect, setRedirect] = useState(false);

    useEffect(() => {
        async function buscaUsuario() {
            const params = new URLSearchParams(location.search);
            const k = params.get('key');
            try {
                const { data } = await api.get(`/usuario/${k}`);
                setUsuario(data);
            } catch (e) {

            }
        }
        buscaUsuario();
    }, [location]);


    const cadastrarUsuario = async (usuario) => {
        try {
            await api.post('/usuario', { usuario });
            notificarSucesso('O usuário foi cadastrado com sucesso.');
            setTimeout(() => {
                setRedirect(true);
            }, 200);
        } catch (e) {
            const { mensagem } = e.response.data;
            notificarErro(mensagem);
        }

    }


    const onFormLayoutChange = ({ size }) => {
        console.log('formChange');
    };

    const onFinish = async (values) => {

        const { nome, funcao, cpfCnpj, senha, confirmSenha } = values;

        if (senha !== confirmSenha) {
            notificarErro('As senhas não são iguais');
        } else {

            const usuario = {
                nome,
                funcao,
                cpfCnpj,
                senha
            };
            cadastrarUsuario(usuario);
        }
    };



    const onFinishFailed = errorInfo => {
        console.log('Failed:', errorInfo);
    };

    const layout = {
        labelCol: {
            span: 5,
        },
        wrapperCol: {
            span: 16,
        },
    };
    const tailLayout = {
        wrapperCol: {
            offset: 5,
            span: 16,
        },
    };
    if (usuario) {
        return (
            <Form
                {...layout}
                name="basic"
                initialValues={{
                    remember: true,
                    nome: usuario.nome,
                }}
                onFinish={onFinish}
                onFinishFailed={onFinishFailed}
                onValuesChange={onFormLayoutChange}
                size='larger'>
                <Form.Item
                    label='Nome'
                    name="nome"
                    rules={[
                        {
                            required: true,
                            message: 'O nome é obrigatorio!'
                        }]}
                >
                    <Input value={usuario.nome} />
                </Form.Item>
                <Form.Item
                    label='Cpf/Cnpj'
                    name="cpfCnpj"
                    rules={[
                        {
                            required: true,
                            message: 'O CPF/Cnpj é obrigatorio!'
                        }]}
                >
                    <Input />
                </Form.Item>
                <Form.Item
                    label='Função'
                    name="funcao"
                    rules={[
                        {
                            required: true,
                            message: 'A Função é obrigatoria!',
                        }]}
                >
                    <Select>
                        {
                            categorias.map((cat) => {
                                return (<Select.Option value={cat} key={cat}>{cat}</Select.Option>)
                            })
                        }
                    </Select>
                </Form.Item>

                <Form.Item
                    label='Senha'
                    name="senha"
                    rules={[
                        {
                            required: true,
                            message: 'A senha é obrigatoria!'
                        }]}
                >
                    <Input />
                </Form.Item>

                <Form.Item
                    label='Confirmação'
                    name="confirmSenha"
                    rules={[
                        {
                            required: true,
                            message: 'A confirmação da senha é obrigatoria!'
                        }]}
                >
                    <Input />
                </Form.Item>

                <Form.Item {...tailLayout}>
                    {atualizar &&
                        <Button
                            type="primary"
                            htmlType="submit">

                            Atualizar
            </Button>
                    }
                    {!atualizar &&
                        <Button
                            type="primary"
                            htmlType="submit">
                            Salvar
            </Button>
                    }
                    <Button
                        type="danger"
                        href="/usuarios"
                        style={{ marginLeft: '2px' }}
                    >
                        Cancelar
                </Button>
                </Form.Item>
            </Form>
        );
    } else {
        return (
            <Form
                {...layout}
                name="basic"
                initialValues={{
                    remember: true
                }}
                onFinish={onFinish}
                onFinishFailed={onFinishFailed}
                onValuesChange={onFormLayoutChange}
                size='larger'>
                <Form.Item
                    label='Nome'
                    name="nome"
                    rules={[
                        {
                            required: true,
                            message: 'O nome é obrigatorio!'
                        }]}
                >
                    <Input value={usuario.nome} />
                </Form.Item>
                <Form.Item
                    label='Cpf/Cnpj'
                    name="cpfCnpj"
                    rules={[
                        {
                            required: true,
                            message: 'O CPF/Cnpj é obrigatorio!'
                        }]}
                >
                    <Input />
                </Form.Item>
                <Form.Item
                    label='Função'
                    name="funcao"
                    rules={[
                        {
                            required: true,
                            message: 'A Função é obrigatoria!',
                        }]}
                >
                    <Select>
                        {
                            categorias.map((cat) => {
                                return (<Select.Option value={cat} key={cat}>{cat}</Select.Option>)
                            })
                        }
                    </Select>
                </Form.Item>

                <Form.Item
                    label='Senha'
                    name="senha"
                    rules={[
                        {
                            required: true,
                            message: 'A senha é obrigatoria!'
                        }]}
                >
                    <Input />
                </Form.Item>

                <Form.Item
                    label='Confirmação'
                    name="confirmSenha"
                    rules={[
                        {
                            required: true,
                            message: 'A confirmação da senha é obrigatoria!'
                        }]}
                >
                    <Input />
                </Form.Item>

                <Form.Item {...tailLayout}>
                    {atualizar &&
                        <Button
                            type="primary"
                            htmlType="submit">

                            Atualizar
                    </Button>
                    }
                    {!atualizar &&
                        <Button
                            type="primary"
                            htmlType="submit">
                            Salvar
                    </Button>
                    }
                    <Button
                        type="danger"
                        href="/usuarios"
                        style={{ marginLeft: '2px' }}
                    >
                        Cancelar
                        </Button>
                </Form.Item>
            </Form>
        );
    }
}

export default FormUsuario;
