import React,{useEffect,useState} from 'react';
import ReactDOM from 'react-dom';
import { Container, Breadcrumb } from 'react-bootstrap';
import Titulo from '../../components/Titulo';
import TabelaUsuarios from '../../components/TabelaUsuarios';
import { Divider, Button } from 'antd';
import FormUsuario from '../../components/FormUsuario';
import {notificarErro,notificarSucesso} from '../../components/Notificacao';
import api from '../../../service/api';
import './index.css';
const { Item } = Breadcrumb;

function Usuarios() {

    const handler_cadastro_button = () => {
        ReactDOM.render(<FormUsuario cadastrar={cadastrarUsuario} update={false}/>,document.getElementById('divUsuarioId'));
    }

    const cadastrarUsuario = async (usuario) => {
        try{
            await api.post('/usuario', { usuario });
            notificarSucesso('O usuário foi cadastrado com sucesso.');
        }catch(e){
           const {mensagem} = e.response.data;
           notificarErro(mensagem);
        }
    }

return (
    <div>
        <Breadcrumb>
            <Item href="/">Principal</Item>
            <Item active>Usuarios</Item>
        </Breadcrumb>
        <Container className='Container'>
            <div id='divUsuarioId'>
                <Titulo name='Usuários' />
                <Divider />
                <TabelaUsuarios/>
                <Divider />
                <Button type='primary' onClick={handler_cadastro_button}>Cadastrar</Button>
            </div>
        </Container>

    </div>
);
}

export default Usuarios;
