import api from './api';

const TOKEN = 'token';
const AVATAR = 'avatar';
const USUARIO_KEY = 'key_usuario';

const getKeyUsuarioLogado = () =>{
    return 3;
}

const isFuncionarioLogado = () => {
    return true;
}

const isClienteLogado = () => {
    return true;
}

const getToken = async () => {
    const token = sessionStorage.getItem(TOKEN);
    return token;
}

const getAvatar = async () => {
    const usuario = await sessionStorage.getItem(AVATAR);
    return usuario;
}

const setToken = async (token) => {
    sessionStorage.setItem(TOKEN,token);
    const {data}= await api.get('/usuarioToken/', {params:{
        Authentication:token
    }});
    console.log(data);
    sessionStorage.setItem(AVATAR,data.avatar);
}

const sair = () => {
    sessionStorage.clear()
}

export {getKeyUsuarioLogado,isFuncionarioLogado,isClienteLogado,getToken,sair,setToken, getAvatar};
