import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import { DeleteOutlined, EditOutlined } from '@ant-design/icons';
import {
    Form,
    Input,
    Button,
    Select,
    InputNumber,
    Divider,
    notification
} from 'antd';

import Menu from '../../components/Menu';
import { Breadcrumb, Container } from 'react-bootstrap';
import Titulo from '../../components/Titulo';
import TabelaHortalicas from '../../components/TabelaHortalicas';
const { Item } = Breadcrumb;


function CadastroPedidos() {
    const acoes = {
        title: 'Inserir',
        dataIndex: 'inserir',
        key: 'inserir',
        render: (text, record) => (
            <span>
                <Button
                    type='primary'
                    style={{ marginLeft: '2px' }}
                    icon={<EditOutlined />}
                    onClick={_ => {
                        alert(record)
                    }}
                />
            </span>
        )
    }

    const hortalicas = [
        {
            id: '1',
            nome: 'Alface',
            categoria: 'Folhas',
            classificacao: '5',
            valor: 3
        },
        {
            id: '2',
            nome: 'Coentro',
            categoria: 'Folhas',
            classificacao: '5',
            valor: 2,
        },
        {
            id: '3',
            nome: 'Cebola',
            categoria: 'Folhas',
            classificacao: '5',
            valor: 5,

        },
    ];

    const [categorias, setCategorias] = useState(['Folhas', 'Temperos', 'Legumes', 'Raizes']);
    const [nome, setNome] = useState('');
    const [categoria, setCategoria] = useState('');
    const [valor, setValor] = useState(0);

    const history = useHistory();

    const onFormLayoutChange = ({ size }) => {
        console.log('formChange');
    };

    const onFinish = values => {
        setNome(values.nome);
        setCategoria(values.categoria);
        setValor(values.valor);
        openNotification();
        setTimeout(redirect, 1000);
    };

    const onFinishFailed = errorInfo => {
        console.log('Failed:', errorInfo);
    };

    const layout = {
        labelCol: {
            span: 5,
        },
        wrapperCol: {
            span: 16,
        },
    };
    const tailLayout = {
        wrapperCol: {
            offset: 5,
            span: 16,
        },
    };

    const openNotification = () => {
        notification.open({
            message: 'Salvo',
            description:
                'A HortaliÃ§a foi salva com sucesso',
            onClick: () => {
                console.log('Notification Clicked!');
            },
        });
    };

    const redirect = () => {
        history.goBack();
    }


    return (
        <div>
            <Menu />
            <Breadcrumb>
                <Item href="/">Principal</Item>
                <Item href="/pedidos">Pedidos</Item>
                <Item active >Cadastrar</Item>
            </Breadcrumb>
            <Container>
                <Titulo name='Cadastro de Pedidos' />
                <Divider />
                <h2>HortaliÃ§as</h2>
                <TabelaHortalicas dataSource={hortalicas} acoes={acoes}/>
                <Divider/>
                <h2>Pedido</h2>

            </Container>
        </div>
    );
}

export default CadastroPedidos;
